private Object decodeLocationNew(
            Channel channel, SocketAddress remoteAddress, String sentence) {
    try {
        // Remove leading and trailing whitespace
        sentence = sentence.trim();

        // Check for valid sentence format (e.g., starts with "$GPGGA")
        if (!sentence.startsWith("$GPGGA")) {
            return null; // Or throw an exception depending on your error handling
        }

        // Split the sentence into parts based on commas
        String[] parts = sentence.split(",");

        // Check if enough parts are present
        if (parts.length < 15) {
            return null; // Or throw an exception
        }

        // Extract relevant data (adjust indices as needed based on your NMEA sentence)
        String time = parts[1];
        String latitude = parts[2];
        String latitudeDirection = parts[3];
        String longitude = parts[4];
        String longitudeDirection = parts[5];
        String fixQuality = parts[6];
        String numberOfSatellites = parts[7];


        //Handle potential null or empty values
        if(time == null || time.isEmpty() || latitude == null || latitude.isEmpty() || longitude == null || longitude.isEmpty()){
            return null;
        }

        // Convert latitude and longitude to decimal degrees
        double latitudeDecimal = convertDMSToDecimal(latitude, latitudeDirection);
        double longitudeDecimal = convertDMSToDecimal(longitude, longitudeDirection);

        // Create a Location object (replace with your actual Location class)
        Location location = new Location(latitudeDecimal, longitudeDecimal);
        location.setTime(time);
        location.setFixQuality(fixQuality);
        location.setNumberOfSatellites(numberOfSatellites);


        return location;

    } catch (Exception e) {
        // Log the exception for debugging
        e.printStackTrace();
        return null; // Or throw a custom exception
    }
}


// Helper function to convert DMS (degrees, minutes, seconds) to decimal degrees
private double convertDMSToDecimal(String dms, String direction) {
    try {
        int degrees = Integer.parseInt(dms.substring(0, 2));
        double minutes = Double.parseDouble(dms.substring(2));
        double decimalDegrees = degrees + minutes / 60.0;
        return direction.equals("S") || direction.equals("W") ? -decimalDegrees : decimalDegrees;
    } catch (NumberFormatException | StringIndexOutOfBoundsException e) {
        return 0; // Or handle the exception appropriately
    }
}

