class HttpUtils {

    static boolean isNonRetryableHTTPCode(CloseableHttpResponse response, boolean retryHTTP403) throws IOException {
        int statusCode = response.getStatusLine().getStatusCode();

        // Consider 400-level errors as non-retryable unless explicitly configured otherwise for 403
        if (statusCode >= 400 && statusCode < 500) {
            if (statusCode == HttpStatus.SC_FORBIDDEN && retryHTTP403) {
                return false; // Retry 403 if configured
            } else {
                return true; // Other 4xx errors are non-retryable
