import java.util.Collection;
import java.util.HashSet;
import java.util.Set;

class Model {
    String name;
    Set<Model> children;

    public Model(String name) {
        this.name = name;
        this.children = new HashSet<>();
    }

    public void addChild(Model child) {
        this.children.add(child);
    }
}

public class PlantUMLGenerator {

    private static String toPlantUml(Model model) {
        StringBuilder sb = new StringBuilder();
        sb.append("@startuml\n");
        generateDiagram(model, sb);
        sb.append("@enduml");
        return sb.toString();
    }

    private static void generateDiagram(Model model, StringBuilder sb) {
        if (model == null) return;
        sb.append(model.name).append("\n");
        for (Model child : model.children) {
            sb.append(model.name).append(" -- ").append(child.name).append("\n");
            generateDiagram(child, sb);
        }
    }


    public static void main(String[] args) {
        Model root = new Model("Root");
        Model child1 = new Model("Child1");
        Model child2 = new Model("Child2");
        Model grandchild1 = new Model("Grandchild1");
        Model grandchild2 = new Model("Grandchild2");

        root.addChild(child1);
        root.addChild(child2);
        child1.addChild(grandchild1);
        child1.addChild(grandchild2);

        String plantUML = toPlantUml(root);
        System.out.println(plantUML);
    }
}
