        // Initialize character to value mapping (example)
        charToValue.put('A', 1);
        charToValue.put('B', 2);
        charToValue.put('C', 3);
        // Add more mappings as needed
    }


    private void decodeFragment(Position position, String fragment) {
        if (position == null || fragment == null || fragment.isEmpty()) {
            return; //Handle null or empty inputs
        }

        int value = 0;
        for (char c : fragment.toCharArray()) {
            Integer val = charToValue.get(c);
            if (val == null) {
                System.err.println("Unknown character: " + c + " in fragment: " + fragment);
                return; // Or handle the error differently, e.g., throw an exception
            }
            value += val;
        }

        // Process the decoded value based on the position
        System.out.println("Position (" + position.x + ", " + position.y + "): Decoded value = " + value);
        // Add your logic here to handle the decoded value and position.  For example, you might update a 2D array.

    }


    public static void main(String[] args) {
        FragmentDecoder decoder = new FragmentDecoder();
        Position pos1 = new Position(1, 2);
        decoder.decodeFragment(pos1, "AB"); //Should print 3

        Position pos2 = new Position(3,4);
        decoder.decodeFragment(pos2, "ABC"); //Should print 6

        Position pos3 = new Position(5,6);
