        this.latitude = latitude;
        this.longitude = longitude;
        this.messageId = messageId;
    }
}

public class IridiumDecoder {

    private Position decodeIridiumPosition(Channel channel, SocketAddress remoteAddress, ByteBuf buf) {
        // Check if enough bytes are available
        if (buf.readableBytes() < 12) {
            return null; // Not enough data
        }

        // Extract data from ByteBuf
        int messageId = buf.readUnsignedShort();
        int latitudeRaw = buf.readUnsignedShort();
        int longitudeRaw = buf.readUnsignedShort();

        // Convert raw data to latitude and longitude (assuming specific Iridium format)
        double latitude = latitudeRaw / 10000.0;
        double longitude = longitudeRaw / 10000.0;


        //Example of extracting IP address from SocketAddress.  May not be needed depending on your use case.
        String ipAddress = null;
        try {
            if (remoteAddress instanceof InetSocketAddress) {
                InetAddress address = ((InetSocketAddress) remoteAddress).getAddress();
                ipAddress = address.getHostAddress();
            }
        } catch (Exception e) {
            //Handle exception appropriately.  Log it, for example.
            System.err.println("Error extracting IP address: " + e.getMessage());
        }


        return new Position(latitude, longitude, messageId);
    }


    public static void main(String[] args) {
        // Example usage (replace with your actual Channel and ByteBuf)
        IridiumDecoder decoder = new IridiumDecoder();
        //Simulate a ByteBuf
