        buf.writeShort(34567890); // latitude (34.567890)
        buf.writeShort(12345678); // longitude (123.456780)

        //Simulate a SocketAddress
        InetSocketAddress address = new InetSocketAddress("192.168.1.100", 5000);

        //Simulate a Channel (replace with your actual channel)
        Channel channel = null; //Replace with your actual channel

        Position position = decoder.decodeIridiumPosition(channel, address, buf);
        if (position != null) {
            System.out.println("Message ID: " + position.messageId);
            System.out.println("Latitude: " + position.latitude);
            System.out.println("Longitude: " + position.longitude);
        } else {
            System.out.println("Not enough data received.");
        }
        buf.release();
    }
}
