import org.jsoup.nodes.Document;
import org.jsoup.nodes.Element;
import org.jsoup.nodes.Node;

import java.io.IOException;

class MyJsoupUtils {
    void outerHtmlHead(Appendable accum, int depth, Document.OutputSettings out) throws IOException {
        Document doc = new Document("http://example.com"); // Dummy document, replace with your actual document
        Element head = doc.head();

        if (head != null) {
            outerHtml(accum, head, depth, out);
        }
    }


    void outerHtml(Appendable accum, Node node, int depth, Document.OutputSettings out) throws IOException {
        if (node.childNodes().size() > 0) {
            accum.append("<").append(node.nodeName());
            for (org.jsoup.nodes.Attribute attr : node.attributes()) {
                accum.append(" ").append(attr.getKey()).append("=").append("\"").append(attr.getValue()).append("\"");
            }
            accum.append(">");
            for (Node child : node.childNodes()) {
                outerHtml(accum, child, depth + 1, out);
            }
            accum.append("</").append(node.nodeName()).append(">");
        } else {
            accum.append("<").append(node.nodeName());
            for (org.jsoup.nodes.Attribute attr : node.attributes()) {
                accum.append(" ").append(attr.getKey()).append("=").append("\"").append(attr.getValue()).append("\"");
            }
            accum.append("/>");
        }
    }
}
