import java.net.MalformedURLException;
import java.net.URL;
import java.net.URLEncoder;
import java.nio.charset.StandardCharsets;

class Solution {
    /**
     * Encodes a URL using UTF-8 encoding.
     *
     * @param u The URL to encode.
     * @return The encoded URL, or null if the input is null or encoding fails.
     */
    static URL encodeUrl(URL u) {
        if (u == null) {
            return null;
        }

        try {
            String encodedProtocol = u.getProtocol();
            String encodedAuthority = u.getAuthority();
            String encodedPath = URLEncoder.encode(u.getPath(), StandardCharsets.UTF_8);
            String encodedQuery = u.getQuery() != null ? URLEncoder.encode(u.getQuery(), StandardCharsets.UTF_8) : null;
            String encodedRef = u.getRef() != null ? URLEncoder.encode(u.getRef(), StandardCharsets.UTF_8) : null;


            StringBuilder encodedUrlBuilder = new StringBuilder();
            encodedUrlBuilder.append(encodedProtocol).append("://");
            if (encodedAuthority != null) {
                encodedUrlBuilder.append(encodedAuthority);
            }
            encodedUrlBuilder.append(encodedPath);
            if (encodedQuery != null) {
                encodedUrlBuilder.append("?").append(encodedQuery);
            }
            if (encodedRef != null) {
                encodedUrlBuilder.append("#").append(encodedRef);
            }

            return new URL(encodedUrlBuilder.toString());
        } catch (MalformedURLException e) {
            // Handle MalformedURLException appropriately, e.g., log the error or throw a custom exception.
            return null; //Returning null in case of error.  Consider alternative error handling.
        }
    }
}
