            target.append(input);
            return target;
        }
        j += BEGIN_TOKEN_LEN;
        String spec = input.substring(j, k);

        String[] items = spec.split(CODE_TEXT_SEPARATOR, 2);
        if (items.length == 1) {
            target.append(input);
            return target;
