                    buf.skipBytes(moduleLength);
                    break;
            }
        }

        if (position.getFixTime() == null) {
            getLastLocation(position, null);
        }

        sendResponse(channel, false, MSG_GPS_MODULAR, buf.readUnsignedShort(), null);

        return position;

