                            break;
                        case 0x14:
                            position.set(Position.KEY_ALARM, Position.ALARM_REMOVING);
                            break;
                        default:
                            break;
                    }
                    position.set(Position.KEY_EVENT, event);
                    break;
                case 0x2e:
