import java.util.concurrent.CompletionStage;

import static akka.pattern.Patterns.ask;

public class CallContextRegistration {

    public static Try<Done> registerCallContext(
            final ActorRef<CallContextProviderMessage> callContextProvider,
            final String callContext,
            final AriCommand ariCommand) {

        // Use ask pattern to send a message to the callContextProvider actor
        CompletionStage<Object> future = ask(callContextProvider, new RegisterCallContextMessage(callContext, ariCommand), Duration.create(5, "seconds"));

        // Handle the future result.  This is a simplified example and error handling should be more robust in production.
        return Try.of(() -> {
            Object result = future.toCompletableFuture().get(); // Blocking call - avoid in production!

            if (result instanceof Done) {
                return (Done) result;
            } else if (result instanceof Throwable) {
                throw (Throwable) result; // Re-throw any exception from the actor
            } else {
                throw new IllegalStateException("Unexpected response from callContextProvider: " + result);
            }
        });
    }


