            this.ariCommand = ariCommand;
        }
    }

    //Simulate Try class - replace with your actual implementation
    static class Try<T> {
        private final T value;
        private final Throwable exception;

        private Try(T value, Throwable exception) {
            this.value = value;
            this.exception = exception;
        }

        public static <T> Try<T> of(java.util.function.Supplier<T> supplier) {
            try {
                return new Try<>(supplier.get(), null);
            } catch (Throwable e) {
                return new Try<>(null, e);
            }
        }

        //Add other methods as needed for your Try implementation
    }
}
