import java.util.Deque;

class RepeatingArgGroup {
    private final String name;

    public RepeatingArgGroup(String name) {
        this.name = name;
    }

    public String getName() {
        return name;
    }
}

public class Solution {
    void consume(final RepeatingArgGroup groupArg, final Deque<NameValuePair> arguments) {
        while (!arguments.isEmpty()) {
            NameValuePair pair = arguments.peekFirst();
            if (pair.getName().equals(groupArg.getName())) {
                arguments.removeFirst(); //Consume the group argument
                //Process the group argument value (pair.getValue()) here if needed.  Example below:
                System.out.println("Consumed group argument: " + pair.getValue());
            } else {
                break; // Stop if we encounter a non-group argument
            }
        }
