import java.util.HashSet;
import java.util.Set;

class MemberId {
    private final String id;

    public MemberId(String id) {
        this.id = id;
    }

    @Override
    public boolean equals(Object obj) {
        if (this == obj) return true;
        if (obj == null || getClass() != obj.getClass()) return false;
        MemberId memberId = (MemberId) obj;
        return id.equals(memberId.id);
    }

    @Override
    public int hashCode() {
        return id.hashCode();
    }
}

public class GameSession {
    private final Set<MemberId> players;
    private final Set<MemberId> spectators;

    public GameSession() {
        this.players = new HashSet<>();
        this.spectators = new HashSet<>();
    }

    public void joinAsSpectator(MemberId memberId) {
        if (memberId == null) {
            throw new IllegalArgumentException("MemberId cannot be null");
        }
        //Avoid adding the same member twice as a spectator.
        spectators.add(memberId);
    }


    public static void main(String[] args) {
        GameSession gameSession = new GameSession();
        MemberId member1 = new MemberId("123");
        gameSession.joinAsSpectator(member1);
        System.out.println(gameSession.spectators.contains(member1)); //true

        MemberId member2 = new MemberId("456");
        gameSession.joinAsSpectator(member2);
        System.out.println(gameSession.spectators.size()); //2

        gameSession.joinAsSpectator(member1); //Adding the same member again.
        System.out.println(gameSession.spectators.size()); //Still 2

        try{
            gameSession.joinAsSpectator(null);
        } catch (IllegalArgumentException e){
            System.out.println("Exception caught successfully: " + e.getMessage());
        }
    }
}
