```java
// return: list of winning candidates in this round (if any)
  private List<String> identifyWinners(
      RoundTally currentRoundTally,
      SortedMap<BigDecimal, LinkedList<String>> currentRoundTallyToCandidates)
      throws TabulationAbortedException {
    List<String> winners = new ArrayList<>();
    BigDecimal quota = currentRoundTally.getQuota();

    //check if any candidate has reached the quota
    for (Map.Entry<BigDecimal, LinkedList<String>> entry : currentRoundTallyToCandidates.entrySet()) {
      BigDecimal tally = entry.getKey();
      if (tally.compareTo(quota) >= 0) {
        LinkedList<String> candidates = entry.getValue();
        winners.addAll(candidates);
      }
    }

    //Handle edge case where no one reaches quota but only one candidate remains.  This candidate wins.
    if(winners.isEmpty() && currentRoundTallyToCandidates.size() == 1){
        winners.addAll(currentRoundTallyToCandidates.get(currentRoundTallyToCandidates.firstKey()));
    }

    return winners;
  }
