```java
private Object decodePosition(
            Channel channel, SocketAddress remoteAddress, String sentence, String id) {
    try {
        //  Example NMEA sentence: $GPGGA,123519,4807.038,N,01131.000,E,1,08,0.9,545.4,M,46.9,M,,*47
        String[] parts = sentence.split(",");

        if (parts.length < 10) {
            System.err.println("Invalid NMEA sentence: " + sentence);
            return null;
        }

        // Check for valid checksum
        String checksum = parts[parts.length - 1].substring(1); //remove *
        String calculatedChecksum = calculateChecksum(sentence.substring(1, sentence.indexOf('*'))); // remove $
        if (!checksum.equals(calculatedChecksum)) {
            System.err.println("Invalid checksum for sentence: " + sentence);
            return null;
        }


        double latitude = parseLatitude(parts[2], parts[3]);
        double longitude = parseLongitude(parts[4], parts[5]);
        int fixQuality = Integer.parseInt(parts[6]);

        if (fixQuality == 0) {
          System.err.println("No GPS fix for sentence: " + sentence);
          return null;
        }

        double altitude = Double.parseDouble(parts[9]);
        String altitudeUnits = parts[10];


        Position position = new Position(latitude, longitude, altitude, altitudeUnits, id, remoteAddress);
        return position;

    } catch (NumberFormatException | IndexOutOfBoundsException e) {
        System.err.println("Error decoding position from sentence: " + sentence + ", Error: " + e.getMessage());
        return null;
    }
}


private String calculateChecksum(String sentence) {
    int checksum = 0;
    for (char c : sentence.toCharArray()) {
        checksum ^= c;
    }
    return Integer.toHexString(checksum).toUpperCase();
}

private double parseLatitude(String latDegrees, String latDirection) {
    double degrees = Double.parseDouble(latDegrees.substring(0, 2));
    double minutes = Double.parseDouble(latDegrees.substring(2));
    double latitude = degrees + minutes / 60.0;
    if (latDirection.equals("S")) {
        latitude *= -1;
