private SimpleRobotRules parseContent(String url, byte[] content, String contentType, Collection<String> robotNames, boolean exactUserAgentMatching) {
    SimpleRobotRules rules = new SimpleRobotRules();

    if (contentType == null || !contentType.startsWith("text/plain")) {
        return rules; // Return empty rules if not a text/plain content type.
    }

    try (InputStream inputStream = new ByteArrayInputStream(content)) {
        String robotsTxtContent = IOUtils.toString(inputStream, StandardCharsets.UTF_8);
        Document doc = Jsoup.parse(robotsTxtContent);

        Elements crawlDelayElements = doc.select("crawl-delay");
        Elements allowElements = doc.select("allow");
        Elements disallowElements = doc.select("disallow");


        //Process robots.txt content here.  This is a skeletal example and needs expansion based on the intended functionality of SimpleRobotRules.
        for (Element element : crawlDelayElements) {
            //Process crawl-delay directives
            System.out.println("Crawl Delay: " + element.text());

        }
        for (Element element : allowElements) {
            //Process allow directives
            System.out.println("Allow: " + element.text());

        }
        for (Element element : disallowElements) {
            //Process disallow directives
            System.out.println("Disallow: " + element.text());
        }


    } catch (IOException e) {
        //Handle exceptions appropriately.  Log the error or throw a custom exception.
        e.printStackTrace();
    }
    return rules;
}
