import org.apache.http.HttpStatus;
import org.apache.http.client.methods.CloseableHttpResponse;
import java.io.IOException;

static boolean isNonRetryableHTTPCode(CloseableHttpResponse response, boolean retryHTTP403) throws IOException {
    int statusCode = response.getStatusLine().getStatusCode();

    // Check for client errors (4xx) except 403 if retryHTTP403 is true.
    if (statusCode >= HttpStatus.SC_BAD_REQUEST && statusCode < HttpStatus.SC_INTERNAL_SERVER_ERROR) {
        if (statusCode == HttpStatus.SC_FORBIDDEN && retryHTTP403) {
            return false; // Retryable 403
        } else {
            return true; // Non-retryable client error
        }
    }

    // Check for server errors (5xx)
    if (statusCode >= HttpStatus.SC_INTERNAL_SERVER_ERROR) {
        return false; // Retryable server error
    }

    // All other status codes are considered non-retryable.  This includes 2xx, 3xx, etc.
    return true;
}
