private Position decodeTransparent(DeviceSession deviceSession, ByteBuf buf) {
    if (buf == null || buf.readableBytes() < 4) {
        // Handle insufficient data
        return null; // Or throw an exception
    }

    int x = buf.readInt();
    int y = buf.readInt();

    return new Position(x, y);
