import java.util.Collection;
import java.util.HashSet;
import java.util.Set;

class Model {
    String name;
    Set<String> attributes;
    Set<String> methods;
    Set<String> associations;


    public Model(String name) {
        this.name = name;
        this.attributes = new HashSet<>();
        this.methods = new HashSet<>();
        this.associations = new HashSet<>();
    }

    public void addAttribute(String attribute) {
        this.attributes.add(attribute);
    }

    public void addMethod(String method) {
        this.methods.add(method);
    }

    public void addAssociation(String association) {
        this.associations.add(association);
    }
}

public class PlantUMLGenerator {

    private static String toPlantUml(Model model) {
        StringBuilder sb = new StringBuilder();
        sb.append("@startuml").append("\n");
        sb.append("class ").append(model.name).append(" {").append("\n");

        for (String attribute : model.attributes) {
            sb.append("  ").append(attribute).append("\n");
        }

        for (String method : model.methods) {
            sb.append("  ").append(method).append("\n");
        }

        sb.append("}").append("\n");


        for (String association : model.associations) {
            String[] parts = association.split(" ");
            if (parts.length == 3) {
                sb.append(parts[0]).append(" -- ").append(parts[2]).append(" : ").append(parts[1]).append("\n");
            } else {
                sb.append("Association not properly formatted: ").append(association).append("\n");
            }
        }

        sb.append("@enduml").append("\n");
        return sb.toString();
    }


    public static void main(String[] args) {
        Model model = new Model("Person");
        model.addAttribute("name: String");
        model.addAttribute("age: int");
        model.addMethod("+getName(): String");
        model.addMethod("-setAge(int age)");
        model.addAssociation("Person -- 1 Car : drives");
        model.addAssociation("Person -- * Address : livesAt");


        System.out.println(toPlantUml(model));
    }
}
