            default:
                buf.skipBytes(length);
                break;
        }
        buf.readerIndex(endIndex);
    }

    if (network.getCellTowers() != null || network.getWifiAccessPoints() != null) {
        position.setNetwork(network);
    }

    sendResponse(channel, MSG_POSITION_RSP, index, null);

