private Position decodeTransparent(DeviceSession deviceSession, ByteBuf buf) {
    if (buf == null || buf.readableBytes() < 8) {
        return null; // Insufficient data
    }

    try {
        double latitude = buf.readDouble();
        double longitude = buf.readDouble();
        double altitude = 0; // Assume altitude is not included in the transparent format, adjust if needed.

        if (buf.readableBytes() >= 8) { //Check if altitude data is available
