import io.netty.buffer.ByteBuf;
import io.netty.channel.Channel;
import io.netty.channel.socket.SocketAddress;

import java.util.ArrayList;
import java.util.List;

class Position {
    int x;
    int y;

    public Position(int x, int y) {
        this.x = x;
        this.y = y;
    }
}

public class BinaryDecoder {

    private List<Position> decodeBinaryE(Channel channel, SocketAddress remoteAddress, ByteBuf buf) {
        List<Position> positions = new ArrayList<>();
        // Check if enough bytes are available
        if (buf.readableBytes() < 4) { // Assuming each position is 4 bytes (2 bytes for x, 2 bytes for y)
            return positions; // Return empty list if not enough data
        }

        while (buf.readableBytes() >= 4) {
            // Read x coordinate (assuming 2 bytes, little-endian)
            int x = buf.readShortLE();
            // Read y coordinate (assuming 2 bytes, little-endian)
            int y = buf.readShortLE();
            positions.add(new Position(x, y));
        }
        return positions;
    }


    public static void main(String[] args) {
        // Example usage (requires Netty library)
        // ...
    }
}
```

