import java.util.Stack;

public int[] calculateSpans(int[] prices) {
    int n = prices.length;
    int[] spans = new int[n];
    Stack<Integer> st = new Stack<>();

    for (int i = 0; i < n; i++) {
        while (!st.isEmpty() && prices[st.peek()] <= prices[i]) {
            st.pop();
        }
        spans[i] = st.isEmpty() ? (i + 1) : (i - st.peek());
        st.push(i);
    }
    return spans;
}
