import java.io.FileOutputStream;
import java.io.IOException;
import java.util.Map;

// param: outputPath is the path to the output file, minus its extension
private void generateSummarySpreadsheet(
        Map<Integer, RoundTally> roundTallies,
        String precinct,
        String outputPath)
        throws IOException {

    Workbook workbook = new XSSFWorkbook(); // Use XSSFWorkbook for xlsx format
    Sheet sheet = workbook.createSheet("Summary");

    // Create header row
    Row headerRow = sheet.createRow(0);
    headerRow.createCell(0).setCellValue("Round");
    headerRow.createCell(1).setCellValue("Total Votes");
    // Add other relevant columns as needed


    int rowNum = 1;
    for (Map.Entry<Integer, RoundTally> entry : roundTallies.entrySet()) {
        int round = entry.getKey();
        RoundTally tally = entry.getValue();

        Row row = sheet.createRow(rowNum++);
        row.createCell(0).setCellValue(round);
        row.createCell(1).setCellValue(tally.getTotalVotes());
        // Add other relevant data from RoundTally to the row

    }

    // Auto-size columns
    for (int i = 0; i < 2; i++) { // Adjust the number based on the number of columns
        sheet.autoSizeColumn(i);
    }


    try (FileOutputStream outputStream = new FileOutputStream(outputPath + ".xlsx")) {
        workbook.write(outputStream);
    }

    workbook.close();
}


