        this.latitude = latitude;
        this.longitude = longitude;
        this.gpsTime = gpsTime;
    }

    @Override
    public String toString() {
        return "Position{" +
                "latitude=" + latitude +
                ", longitude=" + longitude +
                ", gpsTime=" + gpsTime +
                '}';
    }
}


class IridiumDecoder {

    private Position decodeIridiumPosition(Channel channel, SocketAddress remoteAddress, ByteBuf buf) {
        // Check if enough bytes are available
        if (buf.readableBytes() < 12) {
            return null;
        }

        // Assuming the first 12 bytes contain latitude, longitude, and GPS time as follows:
        // Bytes 0-3: Latitude (Integer, representing degrees * 10^7)
        // Bytes 4-7: Longitude (Integer, representing degrees * 10^7)
        // Bytes 8-11: GPS Time (Integer, seconds since epoch)

        int latitudeInt = buf.readInt();
        int longitudeInt = buf.readInt();
        int gpsTime = buf.readInt();

        double latitude = (double) latitudeInt / 10000000;
        double longitude = (double) longitudeInt / 10000000;


        //Basic error checking -  replace with more robust checks as needed.
        if(latitude > 90 || latitude < -90 || longitude > 180 || longitude < -180){
            System.err.println("Invalid Latitude or Longitude received. Discarding data.");
            return null;
        }

        return new Position(latitude, longitude, gpsTime);
    }


    public static void main(String[] args) throws UnknownHostException {
        //Example usage
