import io.netty.buffer.ByteBuf;
import io.netty.channel.Channel;
import io.netty.channel.socket.SocketAddress;

import java.util.ArrayList;
import java.util.List;

class Position {
    public int x;
    public int y;

    public Position(int x, int y) {
        this.x = x;
        this.y = y;
    }
}

public class BinaryDecoder {

    private List<Position> decodeBinaryE(Channel channel, SocketAddress remoteAddress, ByteBuf buf) {
        List<Position> positions = new ArrayList<>();
        // Check if there is enough data to read.  At minimum we need 2 bytes per position (x and y coordinates)
        if (buf.readableBytes() < 2) {
            return positions; // Return empty list if insufficient data
        }

        // Read the number of positions
        int numPositions = buf.readUnsignedShort();


        //Check for valid number of positions
        if(numPositions <0 || numPositions > buf.readableBytes()/2){
            return positions; //return empty list if invalid number of positions
        }

        // Read each position
        for (int i = 0; i < numPositions; i++) {
            int x = buf.readUnsignedShort();
            int y = buf.readUnsignedShort();
            positions.add(new Position(x, y));
        }

        return positions;
    }


