import java.net.URL;
import java.net.URLEncoder;
import java.nio.charset.StandardCharsets;

class Solution {
    /**
     * Encodes a URL using UTF-8 encoding.
     * Handles potential MalformedURLException during URL reconstruction.
     *
     * @param u The URL to encode.
     * @return The encoded URL, or null if encoding fails.
     */
    static URL encodeUrl(URL u) {
        try {
            String protocol = u.getProtocol();
            String userInfo = u.getUserInfo() != null ? URLEncoder.encode(u.getUserInfo(), StandardCharsets.UTF_8) : null;
            String host = u.getHost();
            int port = u.getPort();
            String path = u.getPath() != null ? URLEncoder.encode(u.getPath(), StandardCharsets.UTF_8) : null;
            String query = u.getQuery() != null ? URLEncoder.encode(u.getQuery(), StandardCharsets.UTF_8) : null;
            String ref = u.getRef() != null ? URLEncoder.encode(u.getRef(), StandardCharsets.UTF_8) : null;


            StringBuilder sb = new StringBuilder();
            sb.append(protocol).append("://");
            if (userInfo != null) {
                sb.append(userInfo).append("@");
            }
            sb.append(host);
            if (port != -1) {
                sb.append(":").append(port);
            }
            if (path != null) {
                sb.append(path);
            }
            if (query != null) {
                sb.append("?").append(query);
            }
            if (ref != null) {
                sb.append("#").append(ref);
            }

            return new URL(sb.toString());
        } catch (MalformedURLException e) {
            return null; // Handle exception appropriately, e.g., log the error
        }
    }
}
