                    break;
                case 0x09:
                    position.set(Position.KEY_SATELLITES, buf.readUnsignedByte());
                    break;
                case 0x0a:
                    position.set(Position.KEY_SATELLITES_VISIBLE, buf.readUnsignedByte());
                    break;
                case 0x11:
