    if (!sentence.startsWith("$ERI,")) {
      return null; // or throw an exception depending on error handling strategy.
    }

    // Split the sentence into parts based on the comma delimiter.
    String[] parts = sentence.split(",");

    // Check if we have enough parts.  Adapt based on your expected data.
    if (parts.length < 3) {
        return null; // or throw an exception
    }

    try {
        // Extract relevant data.  Adapt field names and types as needed.
        String messageType = parts[0].substring(1); //remove $ sign
        String deviceId = parts[1];
        String data = parts[2]; //or handle multiple data points.

