import io.netty.buffer.ByteBuf;
import io.netty.channel.Channel;
import io.netty.channel.socket.SocketAddress;

import java.util.ArrayList;
import java.util.List;

class Position {
    public int x;
    public int y;

    public Position(int x, int y) {
        this.x = x;
        this.y = y;
    }
}

public class BinaryDecoder {

    private List<Position> decodeBinaryE(Channel channel, SocketAddress remoteAddress, ByteBuf buf) {
        List<Position> positions = new ArrayList<>();
        // Check if enough bytes are available to read
        if (buf.readableBytes() < 4) {
            return positions; // Return empty list if not enough bytes
        }

        int numPositions = buf.readInt();
        if(numPositions < 0) {
            //Handle negative number of positions - this is an error condition
            return positions;
        }

        for (int i = 0; i < numPositions; i++) {
            //Check if enough bytes are available for each position
            if (buf.readableBytes() < 4) {
                return positions; //Return partially decoded list if not enough bytes
            }
            int x = buf.readInt();
            int y = buf.readInt();
            positions.add(new Position(x, y));
        }
        return positions;
    }


    public static void main(String[] args) {
        BinaryDecoder decoder = new BinaryDecoder();
