import io.netty.buffer.ByteBuf;

private boolean decodeLbs(Position position, ByteBuf buf, int type, boolean hasLength) {
    int length = 0;
    if (hasLength) {
        length = buf.readUnsignedShort(); // Assuming length is a short
        if (buf.readableBytes() < length) {
            return false; // Not enough bytes to read
        }
    }

    switch (type) {
        case 1: // Example type 1 decoding
            position.setLatitude(buf.readDouble());
            position.setLongitude(buf.readDouble());
            position.setAltitude(buf.readFloat());
            break;
        case 2: // Example type 2 decoding.  Adapt as needed for your data format.
            int coordinateLength = buf.readUnsignedByte();
            if (buf.readableBytes() < coordinateLength * 2) return false; // check for enough bytes
            double lat = buf.readShort() / 10000.0; //example conversion, adjust as needed.
            double lon = buf.readShort() / 10000.0; //example conversion, adjust as needed.
            position.setLatitude(lat);
            position.setLongitude(lon);
            break;
