import java.util.List;
import java.util.stream.Collectors;

class EnsembleSummaryView {
    public String ensembleName;
    public List<MemberSummaryView> members;

    public EnsembleSummaryView(String ensembleName, List<MemberSummaryView> members) {
        this.ensembleName = ensembleName;
        this.members = members;
    }
}

class MemberSummaryView {
    public String name;
    public String role;

    public MemberSummaryView(String name, String role) {
        this.name = name;
        this.role = role;
    }
}


class Ensemble {
    public String name;
    public List<MemberId> memberIds;

    public Ensemble(String name, List<MemberId> memberIds) {
        this.name = name;
        this.memberIds = memberIds;
    }
}

class MemberId {
    public String id;

    public MemberId(String id) {
        this.id = id;
    }
}

interface MemberService {
    Member getMember(MemberId memberId);
}

class Member {
    public String name;
    public String role;

    public Member(String name, String role) {
        this.name = name;
        this.role = role;
    }
}


public class EnsembleSummary {

    public static EnsembleSummaryView toView(Ensemble ensemble, MemberId memberId, MemberService memberService) {
        String ensembleName = ensemble.name;
        List<MemberSummaryView> memberViews = ensemble.memberIds.stream()
                .map(memberService::getMember)
                .map(member -> new MemberSummaryView(member.name, member.role))
                .collect(Collectors.toList());

        return new EnsembleSummaryView(ensembleName, memberViews);
    }


    public static void main(String[] args) {
        //Example usage
        MemberService memberService = new MemberService() {
            @Override
            public Member getMember(MemberId memberId) {
                if (memberId.id.equals("1")) return new Member("Alice", "Leader");
                if (memberId.id.equals("2")) return new Member("Bob", "Member");
                return null;
            }
        };

        Ensemble ensemble = new Ensemble("Awesome Ensemble", List.of(new MemberId("1"), new MemberId("2")));
        EnsembleSummaryView view = toView(ensemble, new MemberId("1"), memberService);
        System.out.println(view.ensembleName); // Output: Awesome Ensemble
        System.out.println(view.members.get(0).name); // Output: Alice
        System.out.println(view.members.get(1).name); // Output: Bob

    }
}
