import java.math.BigDecimal;
import java.util.LinkedList;
import java.util.List;
import java.util.SortedMap;

class TabulationAbortedException extends Exception {}

class RoundTally {
    public int getQuota() { return 0; } // Replace with actual implementation
}


// return: list of winning candidates in this round (if any)
private List<String> identifyWinners(
    RoundTally currentRoundTally,
    SortedMap<BigDecimal, LinkedList<String>> currentRoundTallyToCandidates)
    throws TabulationAbortedException {

    List<String> winners = new LinkedList<>();
    int quota = currentRoundTally.getQuota();

    if (quota <=0 ) {
        throw new TabulationAbortedException(); //Handle quota less than or equal to zero.
    }

    for (BigDecimal tally : currentRoundTallyToCandidates.keySet()) {
        if (tally.compareTo(BigDecimal.valueOf(quota)) >= 0) {
            LinkedList<String> candidates = currentRoundTallyToCandidates.get(tally);
            winners.addAll(candidates);
        } else {
            //Optimization: If we've reached a tally below the quota, no need to continue.
            break;
        }
    }
    return winners;
}
