private Object decodePosition(
        Channel channel, SocketAddress remoteAddress, String sentence, String id) {
    // Example structure of sentence: "id:x,y"
    // Where 'id' is the identifier, 'x' is the x-coordinate, and 'y' is the y-coordinate.

    // Split the sentence by the delimiter (e.g., colon)
    String[] parts = sentence.split(":");

    // Check if the sentence has the expected format
    if (parts.length != 2) {
        System.err.println("Invalid sentence format: " + sentence);
        return null; // or throw an exception
    }

    // Extract the position data
    String positionData = parts[1];
    String[] coordinates = positionData.split(",");

    // Ensure we have exactly two coordinates
    if (coordinates.length != 2) {
        System.err.println("Invalid coordinates format: " + positionData);
        return null; // or throw an exception
    }

    try {
        // Parse x and y coordinates
        double x = Double.parseDouble(coordinates[0].trim());
        double y = Double.parseDouble(coordinates[1].trim());

        // Create a position object (could be a custom class or a simple array)
        Position position = new Position(x, y, id); // Assuming Position is a class that takes x, y, and id

        // You may want to process the position further, e.g., logging, channel interaction, etc.
        // Example: channel.writeAndFlush(position);

        return position; // or return some other object if needed
    } catch (NumberFormatException e) {
        System.err.println("Error parsing coordinates: " + positionData + ". " + e.getMessage());
        return null; // or handle the error appropriately
    }
}
