private Position decodeTransparent(DeviceSession deviceSession, ByteBuf buf) {
    // Check if the buffer has enough bytes to read
    if (buf.readableBytes() < 16) { // Example: assumes at least 16 bytes are needed
        return null; // Not enough data to decode
    }

    // Read necessary values from the ByteBuf
    double latitude = buf.readDouble(); // Assuming latitude is a double
    double longitude = buf.readDouble(); // Assuming longitude is a double
    long timestamp = buf.readLong(); // Assuming timestamp is a long

    // Optionally read additional information depending on your protocol
    int speed = buf.readInt(); // Assuming speed is an integer
    int course = buf.readInt(); // Assuming course is an integer
    int satelliteCount = buf.readInt(); // Assuming satellite count is an integer

    // Create a new Position object with the decoded data
    Position position = new Position();
    position.setLatitude(latitude);
    position.setLongitude(longitude);
    position.setTimestamp(timestamp);
    position.setSpeed(speed);
    position.setCourse(course);
    position.setSatelliteCount(satelliteCount);

    // If you need to set the device session related information
    position.setDeviceId(deviceSession.getDeviceId());

    // Return the decoded Position object
    return position;
}
