    accum.append("<title>Your Document Title</title>");

    // Add more elements as needed
    accum.append("<meta charset=\"UTF-8\">");
    accum.append("<meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">");

    // Close the head element
    accum.append("</head>");

    // Optionally add a line break after the head for formatting
    if (out.indent) {
