import io.netty.buffer.ByteBuf;
import io.netty.channel.Channel;
import java.net.SocketAddress;

private Object decodeBasic(Channel channel, SocketAddress remoteAddress, ByteBuf buf) {
    // Check if there is sufficient data in the buffer
    if (buf.readableBytes() < 4) { // Example: checking for at least 4 bytes (for an integer)
        return null; // Not enough data to decode
    }

    // Read the data from the buffer
    int dataType = buf.readInt(); // Example: reading an integer to determine the data type

    Object decodedObject = null;

    // Decoding based on the data type
    switch (dataType) {
        case 1: // Example type 1
            if (buf.readableBytes() < 8) { // Checking for sufficient bytes for this type
                return null;
            }
            long longValue = buf.readLong();
            decodedObject = longValue; // Store the decoded long value
            break;

        case 2: // Example type 2
            if (buf.readableBytes() < 4) {
                return null;
            }
            String stringValue = buf.toString(buf.readerIndex(), 4, Charset.defaultCharset());
            buf.skipBytes(4); // Skip the bytes that were read
            decodedObject = stringValue; // Store the decoded string
            break;

        // Add more cases as needed for different types

        default:
            // Handle unknown type or error
            break;
    }

    // Return the decoded object
    return decodedObject;
}
