                }

            }

        }  else {

            buf.readUnsignedByte(); // overloaded state
            buf.readUnsignedByte(); // logging status

            byte[] alarmStatus = new byte[8];
            buf.readBytes(alarmStatus);

            decodeAlarmStatus(position, alarmStatus);

        }

        return position;

    }

    return null;
}

