            valueCount = buf.readUnsignedByte();
            for (int j = 0; j < valueCount; j++) {
                int id = type == MSG_EXTENDED_RECORDS ? buf.readUnsignedShort() : buf.readUnsignedByte();
                decodeParameter(position, id, buf, 2);
            }

