    } else if (type == MSG_IDENTIFICATION) {

        ByteBuf content = Unpooled.buffer();
        content.writeByte(1);
        ByteBuf response = RuptelaProtocolEncoder.encodeContent(type, content);
        content.release();
        if (channel != null) {
            channel.writeAndFlush(new NetworkMessage(response, remoteAddress));
        }

