public class YourClass {

    private List<Position> decodeBinaryE(Channel channel, SocketAddress remoteAddress, ByteBuf buf) {
        List<Position> positions = new ArrayList<>();

        // Example: Assuming a fixed structure for Position, e.g., (int id, double x, double y)
        while (buf.isReadable()) {
            // Assuming the first byte indicates if there are more positions to read
            if (!buf.isReadable(12)) { // Assuming each Position is 12 bytes
                break; // Not enough data for a full Position
            }

            int id = buf.readInt(); // Read an integer (4 bytes) for ID
            double x = buf.readDouble(); // Read a double (8 bytes) for x-coordinate
            double y = buf.readDouble(); // Read a double (8 bytes) for y-coordinate

            // Create a new Position object and add it to the list
            Position position = new Position(id, x, y);
            positions.add(position);
        }

        return positions;
    }

    // Define the Position class
    static class Position {
        private int id;
        private double x;
        private double y;

        public Position(int id, double x, double y) {
            this.id = id;
            this.x = x;
            this.y = y;
