import io.netty.buffer.ByteBuf;
import io.netty.channel.Channel;
import java.net.SocketAddress;

private Object decodeBasic(Channel channel, SocketAddress remoteAddress, ByteBuf buf) {
    if (buf == null || buf.readableBytes() == 0) {
        return null; // No data to decode
    }

    // Read the length of the message (assuming the first 4 bytes represent the length)
    int length = buf.readInt();

    // Check if there are enough bytes to read
    if (buf.readableBytes() < length) {
        buf.resetReaderIndex(); // Not enough data, reset the reader index
        return null; // Wait for more data
    }

    // Read the message data
    byte[] bytes = new byte[length];
    buf.readBytes(bytes);

    // Convert bytes to a String (or any other object you want to decode into)
    String message = new String(bytes, StandardCharsets.UTF_8);

    // Return the decoded message or any object
    return message;
}
