            break;
        }

        final List<String> argValues = valuesByArg.computeIfAbsent(arg, k -> new ArrayList<>());
        if (!argValues.isEmpty() && !arg.isAllowMultipleValues())
        {
            break;
        }

        SimpleArgumentProcessor.addValue(arg, argument.value, argValues);
        arguments.pollFirst();
    }

