import java.util.HashMap;
import java.util.Map;

public class StringRetriever {
    private Map<String, String> dataStore;

    public StringRetriever() {
        dataStore = new HashMap<>();
    }

    public void put(String key, String value) {
        dataStore.put(key, value);
    }

    public String getString(String key, String defaultValue) {
        return dataStore.getOrDefault(key, defaultValue);
    }

    public static void main(String[] args) {
        StringRetriever retriever = new StringRetriever();
        retriever.put("greeting", "Hello, World!");

        String greeting = retriever.getString("greeting", "Default Greeting");
        System.out.println(greeting); // Output: Hello, World!

        String farewell = retriever.getString("farewell", "Default Farewell");
        System.out.println(farewell); // Output: Default Farewell
    }
}
```

